let $scope;
let settings;
let attributeService;
let utils;
let translate;

self.onInit = function() {
    self.ctx.ngZone.run(function() {
       init(); 
       self.ctx.detectChanges(true);
    });
};


function init() {
    $scope = self.ctx.$scope;
    attributeService = $scope.$injector.get(self.ctx.servicesMap.get('attributeService'));
    utils = $scope.$injector.get(self.ctx.servicesMap.get('utils'));
    translate = $scope.$injector.get(self.ctx.servicesMap.get('translate'));
    $scope.toastTargetId = 'input-widget' + utils.guid();
    settings = utils.deepClone(self.ctx.settings) || {};
    
    settings.showLabel = utils.defaultValue(settings.showLabel, true);
    settings.showResultMessage = utils.defaultValue(settings.showResultMessage, true);
    settings.showGetLocation = utils.defaultValue(settings.showGetLocation, true);
    settings.enableHighAccuracy = utils.defaultValue(settings.enableHighAccuracy, false);
    $scope.settings = settings;
    $scope.isValidParameter = true;
    $scope.dataKeyDetected = false; 

    $scope.isHorizontal = (settings.inputFieldsAlignment === 'row');
    $scope.requiredErrorMessage = utils.customTranslation(settings.requiredErrorMessage, settings.requiredErrorMessage) || translate.instant('widgets.input-widgets.entity-coordinate-required');
    $scope.latLabel = utils.customTranslation(settings.latLabel, settings.latLabel) || translate.instant('widgets.input-widgets.latitude');
    $scope.lngLabel = utils.customTranslation(settings.lngLabel, settings.lngLabel) || translate.instant('widgets.input-widgets.longitude');

    $scope.attributeUpdateFormGroup = $scope.fb.group(
        {currentLat: [undefined, [$scope.validators.required,
                                    $scope.validators.min(-90),
                                    $scope.validators.max(90)]],
        currentLng: [undefined, [$scope.validators.required,
                                    $scope.validators.min(-180),
                                    $scope.validators.max(180)]]}
    );

    if (self.ctx.datasources && self.ctx.datasources.length) {
        var datasource = self.ctx.datasources[0];
        if (datasource.type === 'entity') {
            if (datasource.entityType && datasource.entityId) {
                $scope.entityName = datasource.entityName;
                if (settings.widgetTitle && settings.widgetTitle.length) {
                    $scope.titleTemplate = utils.customTranslation(settings.widgetTitle, settings.widgetTitle);
                } else {
                    $scope.titleTemplate = self.ctx.widgetConfig.title;
                }

                $scope.entityDetected = true;
            }
        }
        if (datasource.dataKeys.length > 1) {
            $scope.dataKeyDetected = true;
            for (let i = 0; i < datasource.dataKeys.length; i++) {
                if (datasource.dataKeys[i].type != "timeseries"){
                    $scope.isValidParameter = false;
                }
                if (datasource.dataKeys[i].name !== settings.latKeyName && datasource.dataKeys[i].name !== settings.lngKeyName){
                    $scope.dataKeyDetected = false;
                }
            }
        }
    }

    self.ctx.widgetTitle = utils.createLabelFromDatasource(self.ctx.datasources[0], $scope.titleTemplate);

    $scope.updateAttribute = function () {
        $scope.isFocused = false;
        if ($scope.entityDetected) {
            var datasource = self.ctx.datasources[0];

            attributeService.saveEntityTimeseries(
                datasource.entity.id,
                'scope',
                [
                    {
                        key: settings.latKeyName,
                        value: $scope.attributeUpdateFormGroup.get('currentLat').value
                    },{
                        key: settings.lngKeyName,
                        value: $scope.attributeUpdateFormGroup.get('currentLng').value
                    }
                ]
            ).subscribe(
                function success() {
                    $scope.originalLat = $scope.attributeUpdateFormGroup.get('currentLat').value;
                    $scope.originalLng = $scope.attributeUpdateFormGroup.get('currentLng').value;
                    if (settings.showResultMessage) {
                        $scope.showSuccessToast(translate.instant('widgets.input-widgets.update-successful'), 1000, 'bottom', 'left', $scope.toastTargetId);
                    }
                },
                function fail() {
                    if (settings.showResultMessage) {
                        $scope.showErrorToast(translate.instant('widgets.input-widgets.update-failed'), 'bottom', 'left', $scope.toastTargetId);
                    }
                }
            );
        }
    };

    $scope.changeFocus = function () {
        if ($scope.attributeUpdateFormGroup.get('currentLat').value === $scope.originalLat && $scope.attributeUpdateFormGroup.get('currentLng').value === $scope.originalLng) {
            $scope.isFocused = false;
        }
    };
    
    $scope.discardChange = function() {
        $scope.attributeUpdateFormGroup.setValue({
            'currentLat': $scope.originalLat,
            'currentLng': $scope.originalLng
        });
        $scope.isFocused = false;
        $scope.attributeUpdateFormGroup.markAsPristine();
        self.onDataUpdated();
    };
    
    $scope.disableButton = function () {
        return $scope.attributeUpdateFormGroup.get('currentLat').value === $scope.originalLat && $scope.attributeUpdateFormGroup.get('currentLng').value === $scope.originalLng || $scope.currentLng === null || $scope.currentLat === null;
